#!/bin/bash\n\n# Build CRaC-enabled images for all Spring Boot services\n# Usage: ./scripts/crac/build-all.sh\n\nset -euo pipefail\n\nSPRING_BOOT_SERVICES=(\"gateway-service\" \"orders-service\" \"billing-service\" \"notification-service\" \"analytics-service\")\n\necho \"=== Building CRaC-enabled images for all Spring Boot services ===\"\necho \"Date: $(date)\"\necho\n\nfor service in \"${SPRING_BOOT_SERVICES[@]}\"; do\n    echo \"Building $service...\"\n    cd \"services/spring-boot/$service\"\n    \n    if docker build -t \"$service:crac\" .; then\n        echo \"✅ $service built successfully\"\n    else\n        echo \"❌ Failed to build $service\"\n        exit 1\n    fi\n    \n    cd - > /dev/null\n    echo\ndone\n\necho \"All Spring Boot services built successfully!\"\necho\necho \"Available CRaC images:\"\ndocker images | grep \":crac\" | awk '{print $1\":\"$2}'\necho\necho \"To create checkpoints for all services:\"\necho \"  ./scripts/crac/checkpoint-all.sh\"\necho\necho \"To create a checkpoint for a specific service:\"\necho \"  ./scripts/crac/checkpoint.sh <service-name>\"